import React, { Component } from 'react';

class About extends Component {
  render() {

    if(this.props.data){
      var name = this.props.data.name;
      var profilepic= "images/"+this.props.data.image;
      var bio1 = this.props.data.bio1;
      var bio2 = this.props.data.bio2;
      var interests = this.props.data.interests;
      var street = this.props.data.address.street;
      var city = this.props.data.address.city;
      var state = this.props.data.address.state;
      var zip = this.props.data.address.zip;
      var phone= this.props.data.phone;
      var email = this.props.data.email;
      var resumeDownload = this.props.data.resumedownload;
    }

   return (
      <section id="about">
         <div class="container">
				<div class="row">
					<div class="col-full">
						<h2 class="section-title">About me</h2>
						<div class="centered line"></div>
					</div>
				</div>
				<div class="row section-content">
					<div class="col-1-3" style="text-align: center;">
						<img alt="This is me!" style="height: 444px;" src="http://www.kaiwa-projects.com/assets/img/photo.jpg"/>
					</div>
					<div class="col-1-3">
						<h4>Hi, It's me!</h4>
						<p>I like building awesome software. I've built websites, browser plugins, corporate software and even games! If you are interested, you can view some of my favorite projects in my <a href="#portfolio">portfolio</a> down below.</p><p>Need something built or simply want to have chat? Reach out to me on social media or send me an <a href="#contact">email</a>.</p>
					</div>
					<div class="col-1-3">
						<h4>My personal interests</h4>
						<p>When I am not <a href="#skills">coding</a> my next project, I enjoy spending my time doing any of the following:</p><ul><li>I love <strong>learning Korean</strong>;</li><li>I am watching my favorite <strong>eSports</strong> games;</li><li>I am <strong>brainstorming</strong> at conferences around the world;</li><li>And I am <strong>meeting more people</strong> and creating new opportunities.</li></ul>					
               </div>
				</div>
			</div>
      </section>
    );
  }
